import React from "react";

export default (Trucks) => {
  const [trucks, setTrucks] = React.useState(null);

  React.useEffect(() => {
    const fetchTrucks = async () => {
      const response = await fetch("./trucks");
      const json = await response.json();

      if (response.ok) {
        setTrucks(json);
      }
    };
    fetchTrucks();
  }, []);

function handleSubmit(e){
    e.preventDefault()
    console.log('You submitted me!')
  }

  return (
    <>
      <div>
        {trucks &&
          trucks.map((truck) => {
            return (
              <div className="truckContainer">
                <div className="truck">
                  <p key={truck._id}><strong>Unit Number:</strong> {truck.unitNumber}</p>
                  <p key={truck._id}><strong>Truck Make:</strong> {truck.unitMake}</p>
                  <p key={truck._id}><strong>Truck Model:</strong> {truck.unitModel}</p>
                </div>
              </div>
            );
          })}

          <form onSubmit={handleSubmit}>
            <input name="unitNumber" placeholder="Unit Number"/>
            <input name="unitMake" placeholder="Unit Make"/>
            <input name="unitModel" placeholder="Unit Model"/>
          </form>
      </div>
    </>
  );
};
