import React from "react";
import NewTruck from './components/widgets/NewTruck'

export default (Trucks) => {
  const [trucks, setTrucks] = React.useState(null);

  React.useEffect(() => {
    const fetchTrucks = async () => {
      const response = await fetch("./trucks");
      const json = await response.json();

      if (response.ok) {
        setTrucks(json);
      }
    };
    fetchTrucks();
  }, []);


  return (
    <>
      <div>
        {trucks &&
          trucks.map((truck) => {
            return (
              <div  key={truck._id} className="truckContainer">
                <div className="truck">
                  <h1 ><strong>{truck.unitNumber}</strong> </h1>
                  <p ><strong>Truck Make:</strong> {truck.unitMake}</p>
                  <p ><strong>Truck Model:</strong> {truck.unitModel}</p>
                  <p ><strong>Truck Mileage:</strong> {truck.unitMileage || 'No Data'}</p>
                  <p ><strong>Truck Type:</strong> {truck.unitType || 'Not Set'}</p>
                </div>
              </div>
            );
          })}

          <NewTruck />
      </div>
    </>
  );
};
